import React from "react";
import "./components.css"
import Button from "./Button"
import warningRedIcon from "../assets/warning-red.svg"
import warningYellowIcon from "../assets/warning-yellow.svg"

class LineItem extends React.Component {
    constructor(props) {
        super(props);
        this.handleRemoveClick = this.handleRemoveClick.bind(this);
        this.handleDomainChange = this.handleDomainChange.bind(this);
        this.warning = this.warning.bind(this);
    }
    handleRemoveClick = () => {
        console.log('button pressed')
        this.props.remove(this.props.item.id);
    };

    handleDomainChange(e) {
        //this.props.item.domain =  e.target.value;
        this.props.editDomain(this.props.item.id,e.target.value );
    }

    warning = (props) => {
        if(props===2){
            return <img width={20} src={warningRedIcon}/> ;
        }
        if(props===0){
            return <img width={20} src={warningYellowIcon}/> ;
        }
    }

    render() {
        let icon = "../assets/warning-red.svg" ;
        return (
            <div className="line-item">
                <form>
                    <input className="line-left" 
                    value={this.props.item.domain}
                    onChange={this.handleDomainChange}/>
                </form>

                <a className="line-right" >{this.props.item.range}</a>
                {this.warning(this.props.item.problemLevel)}
                <Button className="line-button" clickHandler={this.handleRemoveClick}> </Button>
            </div>
        )
    }

}

export default LineItem;
