import React, { Component } from 'react';
import './Dictionary.css';
import Dictionary from './Dictionary'

class Dictionaries extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            dictionary: '',
            history: [],
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.saveToStorage = this.saveToStorage.bind(this);
    }

    handleChange(e) {
        this.setState({ dictionary: e.target.value });
    }

    handleSubmit(e) {
        e.preventDefault();
        if (!this.state.dictionary.length) {
            return;
        }
        const newItem = {
            id: Date.now(),
            name: this.state.dictionary,
        }

        this.setState(state => ({
            items: state.items.concat(newItem),
            dictionary: '',
        }), () => { this.saveToStorage() });

    }
    render() {
        return (
            <div>
                <header className="table-header">
                    Dictionaries
            </header>
                <div className="Dictionaries">
                    <div className="dictionary-list">
                        {this.state.items.map(item => (
                            <Dictionary
                                key={item.id}
                                item={item} />
                        ))}
                    </div>
                    <form onSubmit={this.handleSubmit}>
                        <input
                            className="Form"
                            onChange={this.handleChange}
                            value={this.state.dictionary} />
                        <button className="Button"> Add</button>
                    </form>
                    <button onClick={this.props.change}> Add</button>

                </div>
            </div>
        );
    }
    saveToStorage() {
        let data = this.state.history;
        data.items = this.state.items;
        localStorage.setItem('dictinaries', JSON.stringify(data));
    }
}
export default Dictionaries;
