import React from "react";

class Table extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            number: 0,
            items: [],
        };
    }
    increment = integer => {
        this.setState({number: this.state.number + 1})
    }

    reset = integer => {
        this.setState({number: integer})
    }

    render() {
        const Counter = ({value}) => (
            <div>
              <h1> {value}</h1>
              <button onClick={this.increment}> Add </button>
              <button onClick={()=> (this.reset(0))}> Validate </button>
            </div>
          );
        return (
            <div className="Table">
                <header className="table-header">
                Dictionary 
                </header>
                <Counter value = {this.state.number} />
            </div>
          );
    }
}

export default Table;
