import React, { Component } from 'react';
import './Dictionary.css';
import Dictionary from './Dictionary'

class Dictionaries extends Component {
  constructor(props) {
    super(props);
    this.state = {
        items: ["a","b",'c'],
        dictionary: '',
    };
  }

  handleChange(e) {
    this.setState({ dictionary: e.target.value});
}

handleSubmit(e) {
    e.preventDefault();
    if (!this.state.dictionary.length){
        return;
    }
    const newItem = {
        id: Date.now(),
        name: dictionary,
    }

    this.setState( state =>({
        items: state.items.concat(newItem),
        dictionary: '',
    }),() => {this.saveToStorage()});

}
  render() {

    return (
      <div className="Dictionaries">
        <header className="dict">
        {this.state.items.map(item => (
            <Dictionary key={item.id} 
            item={item} />
          ))}
        </header>
        <form onSubmit={this.handleSubmit}>
                    <input
                    className="Form"  
                    onChange={this.handleChange} 
                    value={this.state.dictionary}/>
                    <button className="Button"> Add</button>
                 </form>
      </div>
    );
  }
    saveToStorage(){
        let data = this.state.history;
        data.items = this.state.items;
        localStorage.setItem('history', JSON.stringify(data));
    }
}
export default Dictionaries;
